```js
/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function(s) {
        if (s.length<2){
            return s
        }
        let res = ''
        for (let i = 0; i < s.length; i++) {
            // ÂõûÊñáÂ≠ê‰∏≤ÈïøÂ∫¶ÊòØÂ•áÊï∞
            helper(i, i)
            // ÂõûÊñáÂ≠ê‰∏≤ÈïøÂ∫¶ÊòØÂÅ∂Êï∞
            helper(i, i + 1) 
        }

        function helper(m, n) {
            while (m >= 0 && n < s.length && s[m] == s[n]) {
                m--
                n++
            }
            // Ê≥®ÊÑèÊ≠§Â§Ñm,nÁöÑÂÄºÂæ™ÁéØÂÆåÂêé  ÊòØÊÅ∞Â•Ω‰∏çÊª°Ë∂≥Âæ™ÁéØÊù°‰ª∂ÁöÑÊó∂Âàª
            // Ê≠§Êó∂mÂà∞nÁöÑË∑ùÁ¶ª‰∏∫n-m+1Ôºå‰ΩÜÊòØmn‰∏§‰∏™ËæπÁïå‰∏çËÉΩÂèñ ÊâÄ‰ª•Â∫îËØ•Âèñm+1Âà∞n-1ÁöÑÂå∫Èó¥  ÈïøÂ∫¶ÊòØn-m-1
            if (n - m - 1 > res.length) {
